import React, { useEffect, useState } from "react";

function Typewriter() {
  const welcomeText = [
    "Welcome",
    "Hello",
    "Hi",
    "Howdy",
    "Greetings",
    "Bonjour",
    "Hey",
    "Hola",
    "Hallo",
    "Ciao",
    "Cześć",
    "Olá",
  ];

  const delay = 50; // Delay between each character
  const wordPause = 2000; // Pause before moving to the next word
  const [currentText, setCurrentText] = useState("");
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const text = welcomeText[currentWordIndex];

    if (currentIndex < text.length) {
      // Typing effect
      const timeout = setTimeout(() => {
        setCurrentText((prevText) => prevText + text[currentIndex]);
        setCurrentIndex((prevIndex) => prevIndex + 1);
      }, delay);

      return () => clearTimeout(timeout);
    } else {
      // After the word is fully typed, wait for `wordPause` ms and reset
      const pauseTimeout = setTimeout(() => {
        setCurrentText(""); // Clear the current text
        setCurrentIndex(0); // Reset character index
        setCurrentWordIndex(
          (prevIndex) => (prevIndex + 1) % welcomeText.length
        ); // Move to the next word, loop around
      }, wordPause);

      return () => clearTimeout(pauseTimeout);
    }
  }, [currentIndex, currentWordIndex, welcomeText]);

  return <span>{currentText}</span>;
}

export default Typewriter;
