import React from "react";

type Props = {
  name: string;
  desc: string;
  img: string;
};

function ProjectCard({ name, desc, img }: Props) {
  return (
    <div className="ProjectCard relative w-96 p-5 rounded-3xl text-black cursor-pointer hover:scale-105 transition-transform duration-300">
      {/* Gradient border on hover */}
      <div className="absolute inset-0 border-2 rounded-3xl border-transparent bg-gradient-to-r from-pink-500 via-purple-500 to-indigo-500 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>

      {/* Card content */}
      <div className="relative bg-white p-5 rounded-3xl z-10 border-2 border-transparent">
        <img
          src={img}
          alt={img}
          className="h-52 w-full object-cover rounded-lg mb-3"
        />
        <h2 className="text-3xl font-bold">{name}</h2>
        <p>{desc}</p>
      </div>
    </div>
  );
}

export default ProjectCard;
