import React, { useEffect, useState } from "react";
import Typewritter from "./components/Typewritter";

function App() {
  const [welcome, setWelcome] = useState("");

  useEffect(() => {
    const welcomeText = [
      "Welcome",
      "Hello",
      "Hi",
      "Howdy",
      "Greetings",
      "Bonjour",
      "Hey",
      "Hola",
      "Hallo",
      "Ciao",
      "Cześć",
      "Olá",
    ];
    let index = 0;

    setInterval(() => {
      setTimeout(() => {
        setWelcome(welcomeText[index]);
      }, 500);

      index += 1;
      if (index > welcomeText.length - 1) index = 0;
    }, 2000);
  }, []);

  return (
    <div className="App grid grid-cols-1 gap-10 xl:grid-cols-3 w-2/3 h-screen m-auto">
      <div className="col-span-2 my-auto">
        <h1 className=" text-9xl font-extralight pb-8">
          <Typewritter text={welcome} delay={50}></Typewritter>
        </h1>

        <p className="font-light text-justify p-3 border-l-2 border-red-500 ">
          Hello, my name is Bartłomiej Mojek. I’m a final-year Computer Science
          student and web developer specializing in responsive web applications
          and React Native apps. I love creating user-friendly solutions with
          HTML, CSS, JavaScript, and frameworks such as React and Node.js.
        </p>
        <p className="font-light text-justify p-3 border-l-2 border-green-500">
          Check out my projects and feel free to get in touch!
        </p>
      </div>

      <div className="col-span-1 my-auto text-2xl grid ">
        <h1>Projects</h1>
      </div>
    </div>
  );
}

export default App;
